(()=>{const s=document.getElementsByClassName("menu");Array.from(s).forEach((s=>{s.addEventListener("click",(()=>{const e=s.nextSibling.nextSibling;"menuItems"===e.className?(e.classList.remove("menuItems"),e.classList.add("visible")):"visible"===e.className&&(e.classList.remove("visible"),e.classList.add("menuItems"))}))}))})();